import React, { Component } from 'react'
import PriceHistoryGraph from '../PriceHistoryGraph'
import axios from 'axios'
import { PageHeader } from 'antd';
import Config from '../../Config';

import { Layout, Menu, Breadcrumb } from 'antd';
import { Card } from 'antd';
const { Header, Content, Footer } = Layout;



export class MinerProfile extends Component {

    state = {
        data: null,
        responseStatus: null
    }

    componentDidMount() {
        console.log(this.props.match.params.address + " miner id")
        axios.post(Config.miner_marketplace_api, {
            query: `query {
                miner(id: "f023534") {
                  id
                  address
                  name
                  bio
                  contact {
                    email
                    slack
                    website
                    twitter
                    email
                  }
                  verified
                  serviceDetails {
                    storage
                    retrieval
                    repair
                    onlineDeals
                    offlineDeals
                    storageAskPrice
                    retrievalAskPrice
                  }
                  financeMetrics {
                    totalIncome
                    blockRewards
                    storageDealPayments
                    retrievalDealPayments
                    networkFee
                    penalty
                    totalExpenditure
                    preCommitDeposits
                    initialPledge
                    lockedFunds
                    availableFunds
                  }
                  transactions (since:500000 till:505914) {
                    transactionType
                    amount
                    sender
                    receiver
                    height
                    timestamp
                    networkFee
                  }
                }
              }`,
            variables: {
                minerId: String(this.props.match.params.address)
            }
        },
        {
            headers: {
                'Content-Type': 'application/json'
            }
        })
        .then(res => {
            console.log(res.data)
            this.setState({data: res.data, responseStatus: "success"})
        })
        .catch(err => {
            console.log(err)
            this.setState({data: null, responseStatus: "failed"})
        })
    }

    render() {
        if (!this.state.responseStatus) {
            return (
                <React.Fragment> 
                    <p>Fetching data from the server...</p>
                </React.Fragment>
            )
        } else {
            if (this.state.data.data.miner) {
                const data = this.state.data.data.miner;
                console.log(data)
                console.log('yo')
                return (
                    <div>
                        <PageHeader
                            className="site-page-header"
                            backIcon="false"
                            title="Miner Profile "
                            subTitle={this.props.match.params.address}
                        />
                        {/* <PriceHistoryGraph data={this.state.data} style={{ width: "80vw", height: "75vh"}}/> */}
                        <Card title="Basic Info" style={{ width: "90vw" }}>
                            <p>Name : { data.name } </p>
                            <p>Bio : { data.bio } </p>
                            <p>Address : { data.address } </p>
                            <p>Id : { data.id } </p>
                            <p>Verified: {data.verified}</p>
                        </Card>
                        <hr></hr>
                        <Card title="Contact" style={{ width: "90vw" }}>
                            <p>Slack : { data.contact.slack } </p>
                            <p>Twitter : { data.contact.twitter } </p>
                            <p>Email : { data.contact.email } </p>
                            <p>Website : { data.contact.website } </p>
                        </Card>
                    </div>
                )
            } else {
                return (
                    <div>Error in fetching data from the server. See console for more details.</div>
                )
            }
        }
    }
}

export default MinerProfile
