    formatFIL(value) {
        if (value === "") {
            value = 0
        } else {
            value = parseInt(value)
        }
        // console.log(value, typeof(value))
        value = value / (10 ** 18)
        // console.log(value)
        value = value.toFixed(4)
        // console.log(value)
        return value.toString() + " FIL"
    }

    formatAdjPower(value) {
        if (value === "") {
            value = 0
        } else {
            value = parseInt(value)
        }
        // console.log(value, typeof(value))
        if (value >= 1024 ** 6) {
            value = value / (1024 ** 6)
            value = value.toFixed(4)
            value = value.toString() + " EiB"
            return value
        }
        if (value >= 1024 ** 5) {
            value = value / (1024 ** 5)
            value = value.toFixed(4)
            value = value.toString() + " PiB"
            return value
        }
        if (value >= 1024 ** 4) {
            value = value / (1024 ** 4)
            value = value.toFixed(4)
            value = value.toString() + " TiB"
            return value
        }
        if (value >= 1024 ** 3) {
            value = value / (1024 ** 3)
            value = value.toFixed(4)
            value = value.toString() + " GiB"
            return value
        }
        if (value >= 1024 ** 2) {
            value = value / (1024 ** 2)
            value = value.toFixed(4)
            value = value.toString() + " MiB"
            return value
        }
        if (value >= 1024) {
            value = value / (1024)
            value = value.toFixed(4)
            value = value.toString() + " KiB"
            return value
        }
        return value.toString() + " bytes"
    }

