import React, { Component } from 'react'
import PriceHistoryGraph from '../PriceHistoryGraph'
import axios from 'axios'
import { PageHeader } from 'antd';
import Config from '../../Config';

import { Layout, Menu, Breadcrumb } from 'antd';
import { Card } from 'antd';
import TransactionTable from '../TransactionTable';
const { Header, Content, Footer } = Layout;



export class MinerProfile extends Component {

    state = {
        data: null,
        responseStatus: null
    }

    componentDidMount() {
        console.log(this.props.match.params.address + " miner id")
        axios.post(Config.miner_marketplace_api, {
            query: `query ($id: ID!) {
                miner(id: $id) {
                    id
                    address
                    name
                    bio
                    owner {
                      id
                      address
                      actor
                      balance
                      messages
                      createdAt
                      latestTransactionAt
                    }
                    worker {
                      id
                      address
                      actor
                      balance
                      messages
                      createdAt
                      latestTransactionAt
                    }
                    contact {
                      email
                      slack
                      website
                      twitter
                      email
                    }
                    verified
                    serviceDetails {
                      storage
                      retrieval
                      repair
                      onlineDeals
                      offlineDeals
                      storageAskPrice
                      retrievalAskPrice
                      minPieceSize
                      maxPieceSize
                    }
                    financeMetrics(since: 560000, till: 570000) {
                      totalIncome
                      totalExpenditure
                      blockRewards
                      storageDealPayments
                      retrievalDealPayments
                      networkFee
                      penalty
                      preCommitDeposits
                      initialPledge
                      lockedFunds
                      availableFunds
                    }
                    qualityIndicators {
                      winCount
                      blocksMined
                      qualityAdjPower
                      feeDebt
                      dataStored
                      faultySectors
                    }
                    transactions(since: 560000, till: 570000) {
                      id
                      height
                      amount
                      sender
                      receiver
                      direction
                      minerFee
                      burnFee
                      actorName
                      methodName
                      exitCode
                    }
                }
              }`,
            variables: {
                id: this.props.match.params.address
            }
        },
            {
                headers: {
                    'Content-Type': 'application/json'
                }
            })
            .then(res => {
                console.log(res.data)
                this.setState({ data: res.data, responseStatus: "success" })
            })
            .catch(err => {
                console.log(err)
                this.setState({ data: null, responseStatus: "failed" })
            })
    }

    formatFIL(value) {
        value = parseInt(value)
        // console.log(value, typeof(value))
        value = value / (10**18)
        // console.log(value)
        value = value.toFixed(4)
        // console.log(value)
        return value.toString() + " FIL"
    }

    render() {
        if (!this.state.responseStatus) {
            return (
                <React.Fragment>
                    <p>Fetching data from the server...</p>
                </React.Fragment>
            )
        } else {
            if (this.state.data.data.miner) {
                const data = this.state.data.data.miner;
                console.log("miner data")
                console.log(data)
                return (
                    <div>
                        <PageHeader
                            className="site-page-header"
                            backIcon="false"
                            title="Miner Profile "
                            subTitle={this.props.match.params.address}
                        />
                        {/* <PriceHistoryGraph data={this.state.data} style={{ width: "80vw", height: "75vh"}}/> */}
                        <Card title="Basic Info" style={{ width: "90vw" }}>
                            <p>Name : {data.name} </p>
                            <p>Bio : {data.bio} </p>
                            <p>Id : {data.id} </p>
                            <p>Owner : {data.owner.id} </p>
                            <p>Worker : {data.worker.id} </p>
                            <p>Verified: {data.verified ? "Yes" : "No"}</p>
                        </Card>
                        <br></br>
                        <Card title="Contact" style={{ width: "90vw" }}>
                            <p>Slack : {data.contact.slack} </p>
                            <p>Twitter : {data.contact.twitter} </p>
                            <p>Email : {data.contact.email} </p>
                            <p>Website : {data.contact.website} </p>
                        </Card>
                        <br></br>
                        <Card title="Service Details" style={{ width: "90vw" }}>
                            <p>Storage : {data.serviceDetails.storage ? "Yes" : "No"} </p>
                            <p>Retrieval : {data.serviceDetails.retrieval ? "Yes" : "No"} </p>
                            <p>Repair : {data.serviceDetails.repair ? "Yes" : "No"} </p>
                            <p>Online Deals : {data.serviceDetails.onlineDeals ? "Yes" : "No"} </p>
                            <p>Offline Deals : {data.serviceDetails.offlineDeals ? "Yes" : "No"} </p>
                            <p>Storage Ask Price : {data.serviceDetails.storageAskPrice} </p>
                            <p>Retrieval Ask Price : {data.retrievalAskPrice} </p>
                        </Card>
                        <br></br>
                        <Card title="Finance Metrics" style={{ width: "90vw" }}>
                            <p>Total Income : {data.financeMetrics.totalIncome} </p>
                            <p>Total Expenditure : {data.financeMetrics.totalExpenditure} </p>
                            <p>Block Rewards : {data.financeMetrics.blockRewards} </p>
                            <p>Storage Deal Payments : {data.financeMetrics.storageDealPayments} </p>
                            <p>Retrieval Deal Payments : {data.financeMetrics.retrievalDealPayments} </p>
                            <p>Network Fee : {data.financeMetrics.networkFee} </p>
                            <p>Penalty : {data.financeMetrics.penalty} </p>
                            <p>PreCommit Deposits : {data.financeMetrics.preCommitDeposits} </p>
                            <p>Initial Pledge : {data.financeMetrics.initialPledge} </p>
                            <p>Locked Funds : {data.financeMetrics.lockedFunds} </p>
                            <p>Available Funds : {data.financeMetrics.availableFunds} </p>
                        </Card>
                        <br></br>
                        <Card title="Quality Indicators" style={{ width: "90vw" }}>
                            <p>Win Count : {data.qualityIndicators.winCount} </p>
                            <p>Blocks Mined : {data.qualityIndicators.blocksMined} </p>
                            <p>Quality Adjusted Power: {data.qualityIndicators.qualityAdjPower} </p>
                        </Card>
                        <br></br>
                        <TransactionTable data={data.transactions}></TransactionTable>
                    </div>
                )
            } else {
                return (
                    <div>Error in fetching data from the server. See console for more details.</div>
                )
            }
        }
    }
}

export default MinerProfile
