    render() {
        return (
            <Router>
                <Layout className="layout">
                    <Header>
                        <Row>
                            <Col span={1}>
                                {/* <Link to="/">
                                    <img src={logo} alt="Logo" style={{ transform: "rotate(-90deg)" }} />
                                </Link> */}
                            </Col>
                            <Col span={17}>
                                <Link to="/">
                                    <h1 style={{ color: "white" }}> Filecoin Miner Marketplace </h1>    
